/*Characters*/

CREATE TABLE IF NOT EXISTS characters (
    id BIGINT AUTO_INCREMENT NOT NULL PRIMARY KEY,
    last_update BIGINT,
    user_id BIGINT REFERENCES `users` (`id`),
    name VARCHAR(255),
    portrait_id BIGINT,
    data VARCHAR(1024),
    param_map BIGINT REFERENCES `param_maps` (`id`),
    is_deleted INT
    ) ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS param_maps (
    id BIGINT AUTO_INCREMENT NOT NULL PRIMARY KEY,
    data VARCHAR(4096)
    ) ENGINE=InnoDB;

/*Portraits*/

CREATE TABLE IF NOT EXISTS portraits (
    id BIGINT AUTO_INCREMENT NOT NULL PRIMARY KEY,
    data BLOB(256000)
    ) ENGINE=InnoDB;

